            set(hotspot[info_text].height, 100px);
            set(hotspot[info_text_moving].html, 'Mithilfe der Pfeile bewegst du dich im Wald an verschiedene Stellen.
			Klicke auf den Pfeil um dich zu bewegen.');
        );
