krpano.action:
alpha_enable('info_hotspot_icon', '0', 'false');
alpha_enable('arrow_hotspot', '0', 'false');
alpha_enable('inventar_icon', '0', 'false');
alpha_enable('inventar_info', '0', 'false');
alpha_enable('inventar', '1', false);
set(hotspot[inventar].atv, calc(get(view.vlookat) - 20));
set(hotspot[inventar].ath, get(view.hlookat));
set(hotspot[mushroom_hotspot].alpha, 1);
set(hotspot[mushroom_hotspot].enabled, false);
set(hotspot[close_icon].alpha, 1);
set(hotspot[close_icon].enabled, true);
set(hotspot[close_icon].ath, calc(get(hotspot[inventar].ath) + 27));
set(hotspot[close_icon].atv, calc(get(hotspot[inventar].atv) - 13));
set(hotspot[close_icon].ry, -30);
 set(hotspot[close_icon].rx, -10);
set(hotspot[mushroom_hotspot].scale, 0.08);
set(hotspot[mushroom_hotspot].ath, calc(get(hotspot[inventar].ath) - 16.5));
set(hotspot[mushroom_hotspot].atv, calc(get(hotspot[inventar].atv) - 7.5));

alpha_enable('info_hotspot_icon', '1', 'true');
alpha_enable('arrow_hotspot', '1', 'true');
alpha_enable('inventar_icon', '1', 'true');
alpha_enable('inventar', '0', false);
set(hotspot[mushroom_hotspot].alpha, 0);
set(hotspot[mushroom_hotspot].enabled, false);
set(hotspot[close_icon].alpha, 0);
set(hotspot[close_icon].enabled, false);
